import Cards from "react-credit-cards";
import React from 'react'
import { Link } from "react-router-dom"
import { useState, useEffect } from 'react'
import 'react-credit-cards/es/styles-compiled.css'

function SnapshotFirebase(){

  const [details, setDetails] = useState({
      cardName: '',
      cardNumber: '',
      cardExpiry: '',
      cardSecurityCode: '',
  });


  useEffect(() => {
      fetch("http://localhost:8080/api/getCards").then(async response => {
          try {
           const data = await response.json()
           console.log(data);
           await setDetails(data);
           console.log('response data?', data)
         } catch(error) {
           console.log('Error happened here!')
           console.error(error)
         }
        })

  })

  const handleClick = async (e) => {
    e.preventDefault()
    try {
        let uid = await firebase.auth().currentUser.uid;  
        if (currID[counter] === uid){
            setDetails({
                ...details,
                cardName: name[counter],
                cardExpiration: expiration[counter],
                cardNumber: number[counter]
            })

            setCounter(counter + 1);
        }  
    } catch(e){
        console.log(e)
    }

  }

  return (
    <div className="container">
      <div className="creditCard">
        <Cards
          expiry={details.cardExpiry}
          name={details.cardName}
          number={details.cardNumber}
        />
        <Link to="/addCard" className="btn btn-primary w-100 mt-3">
            Add Cards
          </Link>
        <button onClick={handleClick}>Next Card</button>

        </div>

      </div>

  );
}

export default SnapshotFirebase;
