function SnapshotFirebase(){

    const [details, setDetails] = useState({});
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchcard = async () => {
            await fetch('/api/getCards')
                .then((res) => res.json());
                setDetails(res.json());
                setIsLoading(false);
        }

    },[]);


